import React from 'react';
import 'antd/dist/antd.css';
import { Menu } from 'antd';

const { SubMenu } = Menu;

// submenu keys of first level
const rootSubmenuKeys = ['sub1', 'sub2', 'sub4'];

export default function Sider({data}){
  const [openKeys, setOpenKeys] = React.useState([]);

  const rootMenu = data.filter(item=>item.parent_id===0);

  const onOpenChange = keys => {
    // const latestOpenKey = keys.find(key => openKeys.indexOf(key) === -1);
    // if (rootSubmenuKeys.indexOf(latestOpenKey) === -1) {
    //   setOpenKeys(keys);
    // } else {
    //   setOpenKeys(latestOpenKey ? [latestOpenKey] : []);
    // }
    setOpenKeys(keys);
  };
  const changeRouter =() =>{
    setOpenKeys(['sub2'])
  }
  
  return (
    <Menu mode="inline" openKeys={openKeys} onOpenChange={onOpenChange}>
      {
        rootMenu.map(category=>
          {
            const branch = data.filter(item=>category.id===item.parent_id);
            return  <SubMenuItem root={category} branch={branch} totalData={data}/>
          }
        )
      }
      {/* <SubMenu key="sub1" title="Navigation One">
        <Menu.Item key="1">Option 1</Menu.Item>
        <Menu.Item key="2">Option 2</Menu.Item>
        <Menu.Item key="3">Option 3</Menu.Item>
        <Menu.Item key="4">Option 4</Menu.Item>
      </SubMenu>
      <SubMenu key="sub2" title="Navigation Two">
        <Menu.Item key="5">Option 5</Menu.Item>
        <Menu.Item key="6">Option 6</Menu.Item>
        <SubMenu key="sub3" title="Submenu">
          <Menu.Item key="7">Option 7</Menu.Item>
          <Menu.Item key="8">Option 8</Menu.Item>
        </SubMenu>
      </SubMenu>
      <SubMenu key="sub4" title="Navigation Three">
        <Menu.Item key="9">Option 9</Menu.Item>
        <Menu.Item key="10">Option 10</Menu.Item>
        <Menu.Item key="11">Option 11</Menu.Item>
        <Menu.Item key="12">Option 12</Menu.Item>
      </SubMenu> */}
    </Menu>
  );
};

function SubMenuItem({root, branch, totalData}){
  // console.log(root, branch);
  return branch.length ?
    <SubMenu key={`category_${root.category_name}`} title={root.category_title} >
      {branch.map(item=>{
          const subBranch = totalData.filter(item1=>item.id===item1.parent_id);
          return <SubMenuItem root={item} branch={subBranch} totalData={totalData}/>
        })}
    </SubMenu>
  :
    <Menu.Item key={`category_${root.category_name}`}>{root.category_title}</Menu.Item>
  
}
