import {category} from '../data/category';

export function getCategory(){
    var root = category.filter(item=>item.parent_id===0);
    makeMenuTree(root);
    //console.log(root)
    // console.log(JSON.stringify(root));
    return root;
}
function makeMenuTree(root){
    root.map(rootitem=>{
        const branch = category.filter(item=>rootitem.id === item.parent_id)
        makeMenuTree(branch);
        Object.assign(rootitem, {...rootitem, children:branch})
    })
}
