import React from 'react';
import Head from 'next/head';
import Link from 'next/link';
import Image from 'next/image';
import clsx from 'clsx';
import { makeStyles, useTheme } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import Drawer from '@material-ui/core/Drawer';
import Hidden from '@material-ui/core/Hidden';
import CssBaseline from '@material-ui/core/CssBaseline';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import IconButton from '@material-ui/core/IconButton';
import Button from '@material-ui/core/Button';
import ButtonGroup from '@material-ui/core/ButtonGroup';
import MenuIcon from '@material-ui/icons/Menu';
import Sider from './menu';
import Content from './content';

import { Input } from "antd";

const {Search} = Input;
const drawerWidth = 220;

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
  },
  logoLink: {
    [theme.breakpoints.down('xs')]: {
      display: 'none',
    },
  },
  appBar: {
    height: theme.spacing(7),
    zIndex: theme.zIndex.drawer + 1,
  },
  appBarShift: {
    width: `calc(100% - ${drawerWidth}px)`,
    marginLeft: drawerWidth,
    transition: theme.transitions.create(['margin', 'width'], {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
  },
  appToolBar: {
    height: theme.spacing(7),
    padding: '0px',
  },
  appBarContainer: {
    height: theme.spacing(7),
    padding: "0px",
  },
  menuButton: {
    [theme.breakpoints.down('xs')]: {
      display: 'none',
    },
  },
  mobileMenuButton: {
    [theme.breakpoints.up('sm')]: {
      display: 'none',
    },
  },
  hide: {
    display: 'none',
  },
  drawer: {
    width: drawerWidth,
    flexShrink: 0,
  },
  drawerPaper: {
    width: drawerWidth,
  },
  drawerContainer: {
    overflow: 'auto',
  },
  drawerHeader: {
    display: 'flex',
    alignItems: 'center',
    padding: theme.spacing(0, 1),
    // necessary for content to be below app bar
    ...theme.mixins.toolbar,
    justifyContent: 'flex-end',
  },
  content: {
    flexGrow: 1,
    padding: theme.spacing(3),
    transition: theme.transitions.create('margin', {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    marginLeft: -drawerWidth,
  },
  contentShift: {
    [theme.breakpoints.up('sm')]: {
      transition: theme.transitions.create('margin', {
        easing: theme.transitions.easing.easeOut,
        duration: theme.transitions.duration.enteringScreen,
      }),
      marginLeft: 0,
    },
  },
}));

export default function Layout() {
  const classes = useStyles();
  const theme = useTheme();
  const [open, setOpen] = React.useState(true);
  const [mobileOpen, setMobileOpen] = React.useState(false);

  const handleMobileDrawerToggle = () => {
    setMobileOpen(!mobileOpen);
  };
  const handleDrawerToggle = () =>{
    setOpen(!open);
  };

  const menuContainer = (
    <div>
      <Toolbar />
      <div className={classes.drawerContainer}>
        <Sider />
      </div>
    </div>
  )

  
const onSearch = (value) => console.log(value);


  return (
    <div className={classes.root}>
      <Head>
        <title>eaglemart.com</title>
        <link rel='icon' href='/favicon.png' />
      </Head>
      <CssBaseline />
      <AppBar
        position="fixed"
        className={clsx(classes.appBar)}
      >
        <Toolbar className={classes.appToolBar}>
          <Container className={clsx(classes.appBarContainer, classes.logoContainer)}>
            <IconButton
              color="inherit"
              aria-label="open drawer"
              onClick={handleDrawerToggle}
              edge="start"
              className={classes.menuButton}
            >
              <MenuIcon />
            </IconButton>
            <IconButton
              color="inherit"
              aria-label="open drawer"
              edge="start"
              onClick={handleMobileDrawerToggle}
              className={classes.mobileMenuButton}
            >
              <MenuIcon />
            </IconButton>
            <Link href='/'>
              <a className={classes.logoLink}>
                <Image src="/lg.png" alt="Logo image" width={150} height={45} />
              </a>
            </Link>

          </Container>
          <Search placeholder="input search text1" onSearch={onSearch} />

          <Container>
              
            <Link href='/help' noWrap><a>Help &amp; More</a></Link>
            <ButtonGroup variant="text" color="secondary" aria-label="text primary button group">
              <Button>En</Button>
              <Button> বাং</Button>
            </ButtonGroup>
          </Container>
            <Button variant="contained" color="secondary">
              Secondary
            </Button>
          
        </Toolbar>
      </AppBar>
      <Hidden smUp implementation="css">
        <Drawer
          className={classes.drawer}
          variant="persistent"
          anchor="left"
          open={mobileOpen}
          classes={{
            paper: classes.drawerPaper,
          }}
        >
          {menuContainer}
        </Drawer>
      </Hidden>
      <Hidden xsDown implementation="css">
        <Drawer
          className={classes.drawer}
          variant="persistent"
          anchor="left"
          open={open}
          classes={{
            paper: classes.drawerPaper,
          }}
        >
          {menuContainer}
        </Drawer>
      </Hidden>

      <main
        className={clsx(classes.content, {
          [classes.contentShift]: open,
        })}
      >
        <Toolbar/>
        <Content />
      </main>
        
    </div>
  );
}
