import {category} from '../data/category';

export function getCategory(){
    var root = category.filter(item=>item.parent_id===0);
    console.log(root);
    makeMenuTree(root);
    return category;
}
function makeMenuTree(root){
    root.map(rootitem=>{
        var branch = category.filter(item=>rootitem.id === item.parent_id)
        Object.assign(item, {...item, children:branch})
    })

    console.log(root);
}
